mapper.select:
    SELECT id, name, imglist, area, model, amount, createtime, distance, tags FROM (
	SELECT t_house.id, t_house.name, t_house.imglist, t_house.area, CONCAT(t_house.room,"室",t_house.parlor,"厅",t_house.toilet,"卫") as model, t_house.amount, t_house.createtime, ROUND(
		6378.138 * 2 * ASIN(
        	SQRT(
                POW(
                	SIN(
                    	(
                          #{lat,jdbcType=DOUBLE} * PI() / 180 - t_house.latitude * PI() / 180
                        ) / 2
                    ),
                    2
                ) + COS( #{lat,jdbcType=DOUBLE} * PI() / 180) * COS( t_house.latitude * PI() / 180) * POW(
                    SIN(
                        (
                            #{lon,jdbcType=DOUBLE} * PI() / 180 - t_house.longitude * PI() / 180
                        ) / 2
                    ),
                    2
                )
        	)
        ) * 1000
   ) AS distance, CONCAT("[",GROUP_CONCAT(t_housetag.tagname),"]") as tags,count(*) as num FROM t_housetag left join t_house on t_housetag.houseid = t_house.id WHERE t_housetag.tagid in ${tags} AND (t_housetag.good-t_housetag.bad) > 0 AND t_house.type = #{type,jdbcType=TINYINT} AND t_house.amount BETWEEN #{min,jdbcType=INTEGER} AND #{max,jdbcType=INTEGER} AND t_house.outdate <= #{date,jdbcType=VARCHAR} AND t_house.tip <= #{tip,jdbcType=INTEGER} AND t_house.state = 1 AND t_house.latitude BETWEEN #{lat,jdbcType=DOUBLE}-0.1 AND #{lat,jdbcType=DOUBLE}+0.1 AND t_house.longitude BETWEEN #{lon,jdbcType=DOUBLE}-0.1 AND #{lon,jdbcType=DOUBLE}+0.1 GROUP BY t_housetag.houseid ORDER BY distance ASC 
	) AS temp where temp.num = #{tagSize,jdbcType=INTEGER} LIMIT #{offset,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

    SELECT id, name, imglist, area, model, amount, createtime, distance, tags FROM (
	SELECT t_house.id, t_house.name, t_house.imglist, t_house.area, CONCAT(t_house.room,"室",t_house.parlor,"厅",t_house.toilet,"卫") as model, t_house.amount, t_house.createtime, ROUND(
		6378.138 * 2 * ASIN(
        	SQRT(
                POW(
                	SIN(
                    	(
                          #{lat,jdbcType=DOUBLE} * PI() / 180 - t_house.latitude * PI() / 180
                        ) / 2
                    ),
                    2
                ) + COS( #{lat,jdbcType=DOUBLE} * PI() / 180) * COS( t_house.latitude * PI() / 180) * POW(
                    SIN(
                        (
                            #{lon,jdbcType=DOUBLE} * PI() / 180 - t_house.longitude * PI() / 180
                        ) / 2
                    ),
                    2
                )
        	)
        ) * 1000
   ) AS distance, CONCAT("[",GROUP_CONCAT(t_housetag.tagname),"]") as tags,count(*) as num FROM t_housetag left join t_house on t_housetag.houseid = t_house.id WHERE t_housetag.tagid in ${tags} AND (t_housetag.good-t_housetag.bad) > 0 AND t_house.room = #{room,jdbcType=INTEGER} AND t_house.type = #{type,jdbcType=TINYINT} AND t_house.amount BETWEEN #{min,jdbcType=INTEGER} AND #{max,jdbcType=INTEGER} AND t_house.outdate <= #{date,jdbcType=VARCHAR} AND t_house.tip <= #{tip,jdbcType=INTEGER} AND t_house.state = 1 AND t_house.latitude BETWEEN #{lat,jdbcType=DOUBLE}-0.1 AND #{lat,jdbcType=DOUBLE}+0.1 AND t_house.longitude BETWEEN #{lon,jdbcType=DOUBLE}-0.1 AND #{lon,jdbcType=DOUBLE}+0.1  GROUP BY t_housetag.houseid ORDER BY distance ASC 
	) AS temp where temp.num = #{tagSize,jdbcType=INTEGER} LIMIT #{offset,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

	SELECT t_house.id, t_house.name, t_house.imglist, t_house.area, CONCAT(t_house.room,"室",t_house.parlor,"厅",t_house.toilet,"卫") as model, t_house.amount, t_house.createtime, ROUND(
		6378.138 * 2 * ASIN(
        	SQRT(
                POW(
                	SIN(
                    	(
                          #{lat,jdbcType=DOUBLE} * PI() / 180 - t_house.latitude * PI() / 180
                        ) / 2
                    ),
                    2
                ) + COS( #{lat,jdbcType=DOUBLE} * PI() / 180) * COS( t_house.latitude * PI() / 180) * POW(
                    SIN(
                        (
                            #{lon,jdbcType=DOUBLE} * PI() / 180 - t_house.longitude * PI() / 180
                        ) / 2
                    ),
                    2
                )
        	)
        ) * 1000
   ) AS distance FROM t_house WHERE t_house.type = #{type,jdbcType=TINYINT} AND t_house.amount BETWEEN #{min,jdbcType=INTEGER} AND #{max,jdbcType=INTEGER} AND t_house.outdate <= #{date,jdbcType=VARCHAR} AND t_house.tip <= #{tip,jdbcType=INTEGER} AND t_house.state = 1 AND t_house.latitude BETWEEN #{lat,jdbcType=DOUBLE}-0.1 AND #{lat,jdbcType=DOUBLE}+0.1 AND t_house.longitude BETWEEN #{lon,jdbcType=DOUBLE}-0.1 AND #{lon,jdbcType=DOUBLE}+0.1 ORDER BY distance ASC LIMIT #{offset,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

	SELECT t_house.id, t_house.name, t_house.imglist, t_house.area, CONCAT(t_house.room,"室",t_house.parlor,"厅",t_house.toilet,"卫") as model, t_house.amount, t_house.createtime, ROUND(
		6378.138 * 2 * ASIN(
        	SQRT(
                POW(
                	SIN(
                    	(
                          #{lat,jdbcType=DOUBLE} * PI() / 180 - t_house.latitude * PI() / 180
                        ) / 2
                    ),
                    2
                ) + COS( #{lat,jdbcType=DOUBLE} * PI() / 180) * COS( t_house.latitude * PI() / 180) * POW(
                    SIN(
                        (
                            #{lon,jdbcType=DOUBLE} * PI() / 180 - t_house.longitude * PI() / 180
                        ) / 2
                    ),
                    2
                )
        	)
        ) * 1000
   ) AS distance FROM t_house WHERE t_house.room = #{room,jdbcType=INTEGER} AND t_house.type = #{type,jdbcType=TINYINT} AND t_house.amount BETWEEN #{min,jdbcType=INTEGER} AND #{max,jdbcType=INTEGER} AND t_house.outdate <= #{date,jdbcType=VARCHAR} AND t_house.tip <= #{tip,jdbcType=INTEGER} AND t_house.state = 1 AND t_house.latitude BETWEEN #{lat,jdbcType=DOUBLE}-0.1 AND #{lat,jdbcType=DOUBLE}+0.1 AND t_house.longitude BETWEEN #{lon,jdbcType=DOUBLE}-0.1 AND #{lon,jdbcType=DOUBLE}+0.1 ORDER BY distance ASC LIMIT #{offset,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}
